#include <iostream>
#include <string>
#include <vector>
#include <sstream>

using namespace std;

const long long MOD = 1000000007;

long long eval(const string& s) {
    istringstream iss(s);
    string token;
    vector<long long> nums;
    while (iss >> token) {
        if (token[0] == 'S') {
            string expr;
            for (size_t i = 1; i < token.size() - 1; ++i) {
                expr += token[i];
            }
            long long x = eval(expr);
            nums.push_back((x * x) % MOD);
        } else {
            nums.push_back(stoll(token));
        }
    }

    long long result = nums[0];
    for (size_t i = 1; i < nums.size(); i += 2) {
        long long y = nums[i];
        result = result >> nums[i + 1];
    }
    return result;
}

int main() {
    string line;
    while (getline(cin, line)) {
        if (line == "#") break;
        cout << eval(line) << endl;
    }
    return 0;
