import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;
import java.util.Comparator;

public class Main {
    static class Island {
        int p, d, idx;

        public Island(int p, int d, int idx) {
            this.p = p;
            this.d = d;
            this.idx = idx;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = 1;
        while (T-- > 0) {
            int n = Integer.parseInt(br.readLine());
            Island[] islands = new Island[n];
            for (int i = 0; i < n; i++) {
                String[] line = br.readLine().split(" ");
                islands[i] = new Island(Integer.parseInt(line[0]), Integer.parseInt(line[1]), i);
            }
            while (n-- > 0) {
                String line = br.readLine();
                if (line.equals("0")) break;
            }
            Arrays.sort(islands, Comparator.comparingInt(a -> a.p));
            int[] parent = new int[n];
            for (int i = 0; i < n; i++) parent[i] = i;
            int[] rank = new int[n];
            for (int i = 0; i < n; i++) rank[i] = 1;
            int[] size = new int[n];
            for (int i = 0; i < n; i++) size[i] = 1;
            int[] cost = new int[n];
            for (int i = 0; i < n; i++) cost[i] = islands[i].p;
            int ans = 0;
            for (int i = 0; i < n; i++) {
                int p = find(parent, islands[i].idx);
                for (int j = i + 1; j < n; j++) {
                    int q = find(parent, islands[j].idx);
                    if (q != p) {
                        if (size[p] < islands[p].d + islands[q].d) {
                            size[p] += size[q];
                            parent[q] = p;
                            cost[p] += Math.abs(islands[p].p - islands[q].p);
                            ans = Math.max(ans, cost[p]);
                        }
                    }
                }
            }
            if (ans == 0) System.out.println(-1);
            else System.out.println(ans);
        }
    }

    static int find(int[] parent, int i) {
        if (parent[i] != i) parent[i] = find(parent, parent[i]);
        return parent[i];
    }
}
