import sys
import heapq

def read_input():
    while True:
        n = int(input())
        if n == 0:
            break
        islands = []
        for _ in range(n):
            p, d = map(int, input().split())
            islands.append((p, d))
        yield islands

def solve(islands):
    n = len(islands)
    points = sorted([island[0] for island in islands])
    edges = []
    for i in range(n):
        for j in range(i + 1, n):
            if islands[i][1] > 0 and islands[j][1] > 0:
                edges.append((abs(points[i] - points[j]), i, j))
    edges.sort()
    parent = list(range(n))
    rank = [0] * n
    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]
    def union(x, y):
        rootx = find(x)
        rooty = find(y)
        if rootx != rooty:
            if rank[rootx] > rank[rooty]:
                parent[rooty] = rootx
            else:
                parent[rootx] = rooty
                if rank[rootx] == rank[rooty]:
                    rank[rooty] += 1
    cost = 0
    for edge in edges:
        if find(edge[1]) != find(edge[2]):
            union(edge[1], edge[2])
            cost += edge[0]
            islands[edge[1]][1] -= 1
            islands[edge[2]][1] -= 1
            if all(island[1] == 0 for island in islands):
                return cost
    return -1

for islands in read_input():
    print(solve(islands))
