#include <math.h>

#define PI 3.14159265358979323846

int main() {
    int u_w, u_h, a, b, ab;
    double x1, y1, r1, x2, y2, r2;

    while (scanf("%d %d %d %d %d", &u_w, &u_h, &a, &b, &ab) == 5) {
        if (u_w == 0 && u_h == 0 && a == 0 && b == 0 && ab == 0) break;

        x1 = (double) (u_w - a + b - ab) / 2;
        y1 = (double) (u_h - a + b - ab) / 2;
        r1 = sqrt((double) (a * a - ab * ab) / PI);

        x2 = (double) (u_w + a - b + ab) / 2;
        y2 = (double) (u_h + a - b + ab) / 2;
        r2 = sqrt((double) (b * b - ab * ab) / PI);

        if (r1 < 0.0001 || r2 < 0.0001 || x1 - r1 < -0.0001 || x1 + r1 > u_w + 0.0001 || y1 - r1 < -0.0001 || y1 + r1 > u_h + 0.0001 ||
            x2 - r2 < -0.0001 || x2 + r2 > u_w + 0.0001 || y2 - r2 < -0.0001 || y2 + r2 > u_h + 0.0001) {
            printf("impossible\n");
        } else {
            printf("%.6f %.6f %.6f %.6f %.6f %.6f\n", x1, y1, r1, x2, y2, r2);
        }
    }

    return 0;
}
