#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAX_H 5
#define MAX_W 5

typedef struct {
    int h, w;
    char initial[MAX_H][MAX_W];
    char target[MAX_H][MAX_W];
} Board;

double paint(Board *board, int i, int j) {
    double total = 0.0;
    for (int x = 0; x <= i; x++) {
        for (int y = 0; y <= j; y++) {
            if (board->initial[x][y] != board->target[x][y]) {
                total += 1.0;
            }
        }
    }
    return total;
}

double expected_painted(Board *board) {
    double total = 0.0;
    for (int i = 0; i < board->h; i++) {
        for (int j = 0; j < board->w; j++) {
            double p = paint(board, i, j) / (i + 1) / (j + 1);
            total += p;
        }
    }
    return total;
}

int main() {
    int h, w;
    while (scanf("%d %d", &h, &w) == 2) {
        Board board;
        board.h = h;
        board.w = w;
        for (int i = 0; i < h; i++) {
            scanf("%s", board.initial[i]);
        }
        scanf("\n");
        for (int i = 0; i < h; i++) {
            scanf("%s", board.target[i]);
        }
        printf("%.6lf\n", expected_painted(&board));
        scanf("\n");
    }
    return 0;
}
