#include <iostream>
#include <vector>
#include <string>
#include <cmath>

using namespace std;

double f(int h, int w, vector<string> &a, vector<string> &b) {
    vector<vector<int>> dp(h + 1, vector<int>(w + 1));
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (a[i - 1][j - 1] == 'B') {
                dp[i][j] = dp[i - 1][j] + dp[i][j - 1] + 1;
            } else {
                dp[i][j] = dp[i - 1][j] + dp[i][j - 1];
            }
        }
    }
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            if (a[i - 1][j - 1] == b[i - 1][j - 1]) {
                dp[i][j] = 0;
            }
        }
    }
    double ans = 0;
    for (int i = 1; i <= h; i++) {
        for (int j = 1; j <= w; j++) {
            ans += (double)(i) * (double)(j) * (double)(dp[i][j]);
        }
    }
    return ans;
}

int main() {
    int h, w;
    while (cin >> h >> w) {
        if (h == 0 && w == 0) {
            break;
        }
        vector<string> a(h), b(h);
        for (int i = 0; i < h; i++) {
            cin >> a[i];
        }
        cin.ignore();
        for (int i = 0; i < h; i++) {
            cin >> b[i];
        }
        cout << fixed << setprecision(10) << f(h, w, a, b) / (double)(h) / (double)(w) << endl;
    }
    return 0;
}
