import sys
import math

def read_input():
    h, w = map(int, input().split())
    initial = [input() for _ in range(h)]
    desired = [input() for _ in range(h)]
    return h, w, initial, desired

def calculate_expected_value(h, w, initial, desired):
    dp = [[0.0 for _ in range(w + 1)] for _ in range(h + 1)]
    for i in range(h, 0, -1):
        for j in range(w, 0, -1):
            if initial[i - 1][j - 1] == 'B':
                dp[i][j] = 1 + dp[i][j + 1] + dp[i + 1][j] - dp[i + 1][j + 1]
            else:
                dp[i][j] = dp[i][j + 1] + dp[i + 1][j] - dp[i + 1][j + 1]
    return dp[1][1] * (h * w)

def main():
    while True:
        h, w, initial, desired = read_input()
        if h == 0 and w == 0:
            break
        print(calculate_expected_value(h, w, initial, desired))

if __name__ == "__main__":
    main()
