#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int MAX_M = 1000;

int M;
vector<string> board;
vector<int> switches;

bool is_valid(int x, int y) {
    return 0 <= x && x < 3 && 0 <= y && y <= 3 * M + 1;
}

bool is_floor(int x, int y) {
    return is_valid(x, y) && board[x][y] >= 'A' && board[x][y] <= 'Z';
}

bool is_wall(int x, int y) {
    return is_valid(x, y) && board[x][y] >= 'a' && board[x][y] <= 'z';
}

bool is_painted(int x, int y) {
    return is_valid(x, y) && board[x][y] >= 'A' && board[x][y] <= 'Z';
}

void dfs(int x, int y, vector<bool>& visited) {
    if (x == 2 && y == 3 * M + 1) {
        cout << switches.size() << " ";
        for (int i = 0; i < switches.size(); i++) {
            cout << (char)('A' + switches[i] - 1);
            if (i != switches.size() - 1) cout << " ";
        }
        cout << endl;
        exit(0);
    }
    visited[x][y] = true;
    for (int dx = -1; dx <= 1; dx++) {
        for (int dy = -1; dy <= 1; dy++) {
            if (dx == 0 && dy == 0) continue;
            int nx = x + dx;
            int ny = y + dy;
            if (is_floor(nx, ny) && !visited[nx][ny]) {
                dfs(nx, ny, visited);
            }
        }
    }
    visited[x][y] = false;
}

int main() {
    while (true) {
        cin >> M;
        if (M == 0) break;
        board.resize(3);
        for (int i = 0; i < 3; i++) {
            cin >> board[i];
        }
        switches.clear();
        for (int i = 0; i < 3; i++) {
            for (int j = 1; j <= 3 * M + 1; j++) {
                if (is_painted(i, j)) {
                    int x = board[i][j] - 'A';
                    if (is_floor(i, j)) {
                        switches.push_back(x);
                    } else {
                        switches.erase(find(switches.begin(), switches.end(), x));
                    }
                }
            }
        }
        vector<bool> visited(3, vector<bool>(3 * M + 2, false));
        dfs(1, 1, visited);
        cout << -1 << endl;
    }
    return 0;
}
