import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static int M;
    static char[][] board;
    static boolean[][] used;
    static boolean[][] walls;
    static boolean[][] switches;
    static boolean[][] visited;
    static List<Character> ans = new ArrayList<>();

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            M = Integer.parseInt(br.readLine());
            board = new char[3][3 * M + 1];
            used = new boolean[3][3 * M + 1];
            walls = new boolean[3][3 * M + 1];
            switches = new boolean[3][3 * M + 1];
            visited = new boolean[3][3 * M + 1];
            for (int i = 0; i < 3; i++) {
                String s = br.readLine();
                for (int j = 0; j < 3 * M + 1; j++) {
                    board[i][j] = s.charAt(j);
                }
            }
            for (int i = 0; i < 3; i++) {
                for (int j = 0; j < 3 * M + 1; j++) {
                    if (board[i][j] >= 'A' && board[i][j] <= 'Z') {
                        switches[i][j] = true;
                    } else if (board[i][j] >= 'a' && board[i][j] <= 'z') {
                        walls[i][j] = true;
                    }
                }
            }
            if (dfs(1, 1)) {
                pw.println(ans.size());
                for (char c : ans) {
                    pw.print(c);
                }
                pw.println();
            } else {
                pw.println(-1);
            }
        }
        pw.close();
    }

    static boolean dfs(int x, int y) {
        if (x == 2 && y == 3 * M + 1) {
            return true;
        }
        if (visited[x][y]) {
            return false;
        }
        visited[x][y] = true;
        for (int i = Math.max(0, x - 1); i <= Math.min(2, x + 1); i++) {
            for (int j = Math.max(0, y - 1); j <= Math.min(3 * M + 1, y + 1); j++) {
                if (used[i][j] || walls[i][j]) {
                    continue;
                }
                if (i == x && j == y + 1) {
                    if (dfs(i, j)) {
                        return true;
                    }
                } else if (i == x && j == y - 1) {
                    if (dfs(i, j)) {
                        return true;
                    }
                } else if (i == x + 1 && j == y) {
                    if (dfs(i, j)) {
                        return true;
                    }
                } else if (i == x - 1 && j == y) {
                    if (dfs(i, j)) {
                        return true;
                    }
                } else if (i == x && j == y) {
                    if (dfs(i, j)) {
                        return true;
                    }
                }
            }
        }
        return false;
    }

    static void push(int x, int y) {
        for (int i = 0; i < 3; i++) {
            for (int j = 0; j < 3 * M + 1; j++) {
                if (switches[i][j] && (i == x || j == y)) {
                    switches[i][j] = false;
                    walls[i][j] = !walls[i][j];
                }
            }
        }
        ans.add((char) ('A' + x));
    }
}
