import sys
from collections import deque

def solve():
    M = int(input())
    board = [input() for _ in range(3)]
    wall = [[False] * (3 * M + 1) for _ in range(3)]
    for i in range(3):
        for j in range(3 * M + 1):
            if board[i][j] != '.':
                wall[i][j] = True

    for i in range(3):
        for j in range(3 * M + 1):
            if board[i][j].isupper():
                for k in range(3 * M + 1):
                    if board[i][k].islower() and board[i][k].lower() == board[i][j]:
                        wall[i][k] = not wall[i][k]

    visited = [[False] * (3 * M + 1) for _ in range(3)]
    queue = deque([(1, 1)])
    visited[1][1] = True
    while queue:
        x, y = queue.popleft()
        if x == 2 and y == 3 * M + 1:
            break
        for dx, dy in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < 3 and 0 <= ny < 3 * M + 1 and not visited[nx][ny] and not wall[nx][ny]:
                queue.append((nx, ny))
                visited[nx][ny] = True

    if not visited[2][3 * M + 1]:
        print(-1)
        return

    ans = []
    for i in range(3):
        for j in range(3 * M + 1):
            if board[i][j].isupper():
                for k in range(3 * M + 1):
                    if board[i][k].islower() and board[i][k].lower() == board[i][j]:
                        wall[i][k] = not wall[i][k]
                if not visited[i][j]:
                    ans.append(board[i][j])
                for k in range(3 * M + 1):
                    if board[i][k].islower() and board[i][k].lower() == board[i][j]:
                        wall[i][k] = not wall[i][k]

    print(len(ans), *ans)

def main():
    T = 1
    while T:
        solve()
        T -= 1

if __name__ == '__main__':
    main()
