import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (scanner.hasNextLine()) {
            String line = scanner.nextLine();
            if (line.equals("#")) {
                break;
            }
            System.out.println(calculateAngle(line));
        }
        scanner.close();
    }

    public static String calculateAngle(String direction) {
        int northCount = 0;
        int westCount = 0;
        for (int i = 0; i < direction.length(); i++) {
            if (direction.charAt(i) == 'n') {
                northCount++;
            } else if (direction.charAt(i) == 'w') {
                westCount++;
            }
        }
        int baseAngle = 0;
        for (int i = 0; i < direction.length(); i++) {
            if (direction.charAt(i) == 'n') {
                baseAngle = 0;
            } else if (direction.charAt(i) == 'w') {
                baseAngle = 90;
            } else {
                baseAngle = baseAngle * 10 + (direction.charAt(i) - '0');
            }
        }
        double angle = baseAngle - (90.0 / Math.pow(2, northCount - 1)) + (90.0 / Math.pow(2, westCount - 1));
        if (angle % 1 == 0) {
            return String.valueOf((int) angle);
        } else {
            return String.valueOf(angle);
        }
    }
}
