#include <stdio.h>
#include <string.h>

#define N 36
#define M 1000
#define Q 1000

int n, m, q;
int s[Q][N], b[Q][M];
int ans[M];

void solve() {
    int i, j, k, l, x, y, z;
    int mask[N], bit[M];

    scanf("%d %d %d", &n, &m, &q);
    for (i = 0; i < q; i++) {
        scanf("%s %s", s[i], b[i]);
        for (j = 0; j < n; j++) {
            mask[j] = (s[i][j] == '1') ? (1 << j) : 0;
        }
        for (j = 0; j < m; j++) {
            bit[j] = (b[i][j] == '1') ? (1 << j) : 0;
        }
    }

    for (i = 0; i < m; i++) {
        ans[i] = '?';
    }

    for (i = 0; i < q; i++) {
        for (j = 0; j < n; j++) {
            mask[j] = (s[i][j] == '1') ? (1 << j) : 0;
        }
        for (j = 0; j < m; j++) {
            bit[j] = (b[i][j] == '1') ? (1 << j) : 0;
        }

        for (j = 0; j < m; j++) {
            if (bit[j] == 0) continue;
            for (k = 0; k < n; k++) {
                if ((mask[k] & bit[j]) == 0) continue;
                for (l = 0; l < m; l++) {
                    if (bit[l] == 0) continue;
                    if ((bit[j] & bit[l]) == bit[l]) {
                        ans[l] = k;
                        break;
                    }
                }
            }
        }
    }

    for (i = 0; i < m; i++) {
        if (ans[i] == '?') {
            for (j = 0; j < n; j++) {
                if (ans[i] != '?' && ans[i] != j) {
                    ans[i] = j;
                    break;
                }
            }
        }
        printf("%c", ans[i] + '0');
    }
    printf("\n");
}

int main() {
    int i;

    while (scanf("%d %d %d", &n, &m, &q) == 3) {
        if (n == 0 && m == 0 && q == 0) break;
        solve();
    }

    return 0;
}
