#include <iostream>
#include <vector>
#include <string>
#include <bitset>
#include <algorithm>

using namespace std;

int main() {
    int n, m, q;
    while (cin >> n >> m >> q) {
        if (n == 0 && m == 0 && q == 0) break;

        vector<string> s(q), b(q);
        for (int i = 0; i < q; i++) {
            cin >> s[i] >> b[i];
        }

        vector<int> mask(1 << n);
        for (int i = 0; i < (1 << n); i++) {
            for (int j = 0; j < n; j++) {
                if ((i >> j) & 1) {
                    mask[i] |= 1 << j;
                }
            }
        }

        vector<int> state(1 << m);
        for (int i = 0; i < (1 << m); i++) {
            for (int j = 0; j < m; j++) {
                if ((i >> j) & 1) {
                    state[i] |= 1 << j;
                }
            }
        }

        vector<int> ans(m);
        for (int i = 0; i < q; i++) {
            int cur = 0;
            for (int j = 0; j < n; j++) {
                if (s[i][j] == '1') {
                    cur |= 1 << j;
                }
            }

            for (int j = 0; j < (1 << m); j++) {
                if ((state[j] & cur) == state[j]) {
                    ans[j] |= 1 << i;
                }
            }

            for (int j = 0; j < m; j++) {
                if (b[i][j] == '1') {
                    state[j] |= cur;
                } else {
                    state[j] &= ~cur;
                }
            }
        }

        for (int i = 0; i < m; i++) {
            int cnt = 0;
            for (int j = 0; j < n; j++) {
                if ((ans[i] >> j) & 1) {
                    cnt++;
                }
            }

            if (cnt == 1) {
                for (int j = 0; j < n; j++) {
                    if ((ans[i] >> j) & 1) {
                        cout << char('0' + j);
                        break;
                    }
                }
            } else {
                cout << '?';
            }
        }

        cout << endl;
    }

    return 0;
}
