import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.*;

public class Main {
    static int N, M, Q;
    static int[][] S, B;
    static int[] ans;
    static int[] mask;
    static int[] bit;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        int T = 1;
        while (T-- > 0) {
            String[] line = br.readLine().split(" ");
            N = Integer.parseInt(line[0]);
            M = Integer.parseInt(line[1]);
            Q = Integer.parseInt(line[2]);
            S = new int[Q][N];
            B = new int[Q][M];
            for (int i = 0; i < Q; i++) {
                line = br.readLine().split(":");
                String[] s = line[0].split("");
                String[] b = line[1].split("");
                for (int j = 0; j < N; j++) {
                    S[i][j] = s[j].equals("1") ? 1 : 0;
                }
                for (int j = 0; j < M; j++) {
                    B[i][j] = b[j].equals("1") ? 1 : 0;
                }
            }
            ans = new int[M];
            mask = new int[1 << N];
            bit = new int[1 << N];
            Arrays.fill(mask, -1);
            for (int i = 0; i < Q; i++) {
                int cur = 0;
                for (int j = 0; j < N; j++) {
                    if (S[i][j] == 1) {
                        cur ^= (1 << j);
                    }
                }
                for (int j = 0; j < M; j++) {
                    if (B[i][j] == 1) {
                        bit[cur]++;
                    }
                }
                for (int j = 0; j < (1 << N); j++) {
                    if (mask[j] != -1) {
                        continue;
                    }
                    if (bit[j ^ cur] == bit[cur]) {
                        mask[j ^ cur] = j;
                    }
                }
            }
            for (int i = 0; i < M; i++) {
                int cur = 0;
                for (int j = 0; j < (1 << N); j++) {
                    if (bit[j] == 1 && (j & cur) == 0) {
                        cur ^= j;
                    }
                }
                for (int j = 0; j < N; j++) {
                    if ((cur & (1 << j)) != 0) {
                        ans[i] = (char) ('A' + j);
                        break;
                    }
                }
                if (ans[i] == 0) {
                    ans[i] = '?';
                }
            }
            for (int i = 0; i < M; i++) {
                pw.print(ans[i]);
            }
            pw.println();
        }
        pw.close();
    }
}
