#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100000

typedef struct {
    double vw, pf, vf, th;
} flower;

int main() {
    int n, pw;
    flower f[N];
    double cost, w, x, y, z;

    while (scanf("%d", &n) == 1 && n != 0) {
        scanf("%d", &pw);
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf %lf %lf", &f[i].vw, &f[i].pf, &f[i].vf, &f[i].th);
        }
        cost = 1e100;
        for (int i = 0; i < n; i++) {
            w = (f[i].th - f[i].vw * 1e4) / (f[i].vf * 1e4);
            if (w < 0) w = 0;
            x = f[i].pf * w;
            y = f[i].pf * w * w;
            z = f[i].pf * w * w * w;
            cost = fmin(cost, 1e4 * pw * w + x + y + z);
        }
        printf("%.1f\n", cost);
    }
    return 0;
}
