#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

struct Flower {
    double vw, pf, vf, th;
};

bool compareFlowers(Flower a, Flower b) {
    return a.th < b.th;
}

double solve(vector<Flower>& flowers, int pw) {
    double w = 0;
    double cost = 0;
    for (int i = 0; i < flowers.size(); i++) {
        double f = (flowers[i].th - w * flowers[i].vw) / flowers[i].vf;
        if (f > w) {
            w = f;
            cost += w * pw;
        }
        cost += flowers[i].pf * f;
    }
    return cost;
}

int main() {
    int n;
    while (cin >> n && n != 0) {
        int pw;
        cin >> pw;
        vector<Flower> flowers(n);
        for (int i = 0; i < n; i++) {
            cin >> flowers[i].vw >> flowers[i].pf >> flowers[i].vf >> flowers[i].th;
        }
        sort(flowers.begin(), flowers.end(), compareFlowers);
        double cost = solve(flowers, pw);
        cout << fixed << setprecision(4) << cost << endl;
    }
    return 0;
}
