import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.PrintWriter;
import java.util.StringTokenizer;

public class Main {
    static class InputReader {
        BufferedReader br;
        StringTokenizer st;

        public InputReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }
    }

    public static void main(String[] args) {
        InputReader in = new InputReader();
        PrintWriter out = new PrintWriter(System.out);

        int T = in.nextInt();
        while (T-- > 0) {
            int n = in.nextInt();
            long pw = in.nextLong();
            long[] vw = new long[n + 1];
            long[] pf = new long[n + 1];
            long[] vf = new long[n + 1];
            long[] th = new long[n + 1];
            for (int i = 1; i <= n; i++) {
                vw[i] = in.nextLong();
                pf[i] = in.nextLong();
                vf[i] = in.nextLong();
                th[i] = in.nextLong();
            }
            long ans = 0;
            for (int i = 1; i <= n; i++) {
                long w = (th[i] - vw[i] * ans) / vf[i];
                if (w < 0) {
                    out.println("inf");
                    break;
                }
                ans += w;
                ans += (th[i] - vw[i] * ans) * 1.0 / vf[i] * pf[i] / pw;
            }
            if (T > 0) out.println();
        }
        out.close();
    }
}
