#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

double solve(int n, vector<int>& x, vector<int>& r) {
    double ans = 0;
    for (int i = 0; i < n; i++) {
        double d = x[i] + r[i];
        if (i == 0) {
            ans = d;
        } else {
            double d2 = x[i] - x[i - 1] - r[i - 1];
            if (d2 > 0) {
                d = min(d, d2);
            }
        }
        ans = min(ans, d);
    }
    return 2 * ans;
}

int main() {
    int n;
    while (cin >> n) {
        if (n == 0) break;
        vector<int> x(n), r(n);
        for (int i = 0; i < n; i++) {
            cin >> x[i] >> r[i];
        }
        cout << fixed << setprecision(10) << solve(n, x, r) << endl;
    }
    return 0;
}
