import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        while (n != 0) {
            double[] x = new double[n + 1];
            double[] r = new double[n + 1];
            for (int i = 1; i <= n; i++) {
                x[i] = scanner.nextDouble();
                r[i] = scanner.nextDouble();
            }
            System.out.println(solve(x, r, n));
            n = scanner.nextInt();
        }
        scanner.close();
    }

    public static double solve(double[] x, double[] r, int n) {
        double[] d = new double[n + 1];
        for (int i = 1; i <= n; i++) {
            d[i] = x[i] - x[i - 1];
        }
        double[] s = new double[n + 1];
        for (int i = 1; i <= n; i++) {
            s[i] = r[i] + r[i - 1];
        }
        double[] f = new double[n + 1];
        for (int i = 1; i <= n; i++) {
            f[i] = Math.min(d[i], s[i]);
        }
        double[] g = new double[n + 1];
        for (int i = 1; i <= n; i++) {
            g[i] = Math.max(d[i] - s[i], 0);
        }
        double[] h = new double[n + 1];
        for (int i = 1; i <= n; i++) {
            h[i] = Math.max(f[i] - g[i], 0);
        }
        double ans = 0;
        for (int i = 1; i <= n; i++) {
            ans = Math.max(ans, h[i]);
        }
        return ans;
    }
}
