#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 1000;
const int MAX_M = 10000;

int n, m, s, t;
vector<int> g[MAX_N];
vector<int> rg[MAX_N];
int dist[MAX_N];
int dist2[MAX_N];
int in[MAX_N];
int out[MAX_N];
int maxFlow;
int maxFlow2;
int ans;

void bfs() {
    fill(dist, dist + n, -1);
    queue<int> q;
    q.push(s);
    dist[s] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            if (dist[v] == -1 && rg[u][v] > 0) {
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
}

int dfs(int u, int f) {
    if (u == t) {
        return f;
    }
    for (int &i = in[u]; i < g[u].size(); ++i) {
        int v = g[u][i];
        if (dist[v] == dist[u] + 1 && rg[u][v] > 0) {
            int d = dfs(v, min(f, rg[u][v]));
            if (d > 0) {
                rg[u][v] -= d;
                rg[v][u] += d;
                return d;
            }
        }
    }
    return 0;
}

void dinic() {
    maxFlow = 0;
    while (true) {
        bfs();
        if (dist[t] == -1) {
            break;
        }
        fill(in, in + n, 0);
        while (true) {
            int f = dfs(s, 1e9);
            if (f == 0) {
                break;
            }
            maxFlow += f;
        }
    }
}

void dinic2() {
    maxFlow2 = 0;
    while (true) {
        bfs();
        if (dist2[t] == -1) {
            break;
        }
        fill(in, in + n, 0);
        while (true) {
            int f = dfs(s, 1e9);
            if (f == 0) {
                break;
            }
            maxFlow2 += f;
        }
    }
}

int main() {
    while (true) {
        cin >> n >> m >> s >> t;
        if (n == 0) {
            break;
        }
        s--;
        t--;
        for (int i = 0; i < n; ++i) {
            g[i].clear();
            rg[i].clear();
        }
        for (int i = 0; i < m; ++i) {
            int u, v;
            cin >> u >> v;
            u--;
            v--;
            g[u].push_back(v);
            rg[v].push_back(u);
        }
        dinic();
        ans = maxFlow;
        fill(dist2, dist2 + n, -1);
        for (int i = 0; i < m; ++i) {
            int u = g[i].size() > 1 ? g[i][0] : s;
            int v = g[i].size() > 1 ? g[i][1] : t;
            swap(g[u][v], rg[u][v]);
            swap(rg[v][u], g[v][u]);
            dinic2();
            if (maxFlow2 > ans) {
                ans = maxFlow2;
            }
            swap(g[u][v], rg[u][v]);
            swap(rg[v][u], g[v][u]);
        }
        cout << ans << " " << (ans == maxFlow ? 0 : 1) << endl;
    }
    return 0;
}
