from collections import deque
import sys

def bfs(s, t, g, r):
    q = deque()
    q.append(s)
    dist = [-1] * (n + 1)
    dist[s] = 0
    parent = [-1] * (n + 1)
    while q:
        u = q.popleft()
        for v in g[u]:
            if dist[v] == -1:
                dist[v] = dist[u] + 1
                parent[v] = u
                q.append(v)
            elif dist[v] == dist[u] + 1:
                r[u][v] = 1
                r[v][u] = 0
    return dist[t] != -1

def dfs(u, t, g, r, visited):
    if u == t:
        return 1
    visited[u] = True
    for v in g[u]:
        if not visited[v] and r[u][v]:
            if dfs(v, t, g, r, visited):
                return 1
    return 0

def max_flow(s, t, g, r):
    max_flow = 0
    while bfs(s, t, g, r):
        visited = [False] * (n + 1)
        while dfs(s, t, g, r, visited):
            max_flow += 1
    return max_flow

def solve():
    global n, m, s, t, g, r
    n, m, s, t = map(int, input().split())
    g = [[] for _ in range(n + 1)]
    r = [[0] * (n + 1) for _ in range(n + 1)]
    for _ in range(m):
        u, v = map(int, input().split())
        g[u].append(v)
        r[u][v] = 1
    g[s].append(t)
    r[s][t] = 1
    max_flow = max_flow(s, t, g, r)
    g[s].pop()
    r[s][t] = 0
    max_flow_no_reverse = max_flow(s, t, g, r)
    if max_flow_no_reverse == max_flow:
        print(max_flow, 0)
    else:
        print(max_flow, 1)

t = 1
for _ in range(t):
    solve()
