#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

long long lcm(long long a, long long b) {
    return a / gcd(a, b) * b;
}

int main() {
    int n, d, x;
    long long N, D, X;
    int i, j;
    long long ans = 0;
    long long ways[2001];

    while (scanf("%lld %lld %lld", &N, &D, &X) == 3) {
        if (N == 0 && D == 0 && X == 0) break;

        ways[0] = 1;
        for (i = 1; i <= X; i++) ways[i] = 0;

        for (i = 1; i <= N; i++) {
            for (j = X; j >= 1; j--) {
                ways[j] = (ways[j] + ways[j - 1]) % MOD;
            }
        }

        ans = 0;
        for (i = 1; i <= X; i++) {
            long long temp = N / i;
            long long temp2 = lcm(D, i);
            long long temp3 = N / temp2;
            ans = (ans + ways[i] * temp3) % MOD;
        }

        printf("%lld\n", ans);
    }

    return 0;
}
