#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

const int MOD = 1000000007;

int main() {
    std::vector<int> N, D, X;
    int n;
    while (std::cin >> n >> n >> n) {
        if (n == 0 && n == 0 && n == 0) break;
        N.push_back(n);
        D.push_back(n);
        X.push_back(n);
    }

    for (int i = 0; i < N.size(); i++) {
        int n = N[i];
        long long d = D[i];
        int x = X[i];

        std::vector<int> dp(x + 1);
        dp[0] = 1;

        for (int j = 1; j <= d; j++) {
            std::vector<int> ndp(x + 1);
            for (int k = 0; k <= x; k++) {
                for (int l = 1; l <= std::min(k, x); l++) {
                    ndp[l] = (ndp[l] + dp[k - l]) % MOD;
                }
            }
            dp = ndp;
        }

        int ans = 0;
        for (int j = 0; j <= x; j++) {
            ans = (ans + dp[j]) % MOD;
        }

        std::cout << ans << std::endl;
    }

    return 0;
}
