import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        double x, y;

        public Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Line {
        double a, b, c;

        public Line(double a, double b, double c) {
            this.a = a;
            this.b = b;
            this.c = c;
        }
    }

    static Point parsePoint(String s) {
        s = s.substring(1, s.length() - 1);
        String[] parts = s.split(",");
        return new Point(Double.parseDouble(parts[0]), Double.parseDouble(parts[1]));
    }

    static Line parseLine(String s) {
        s = s.substring(1, s.length() - 1);
        String[] parts = s.split("@");
        Point p1 = parsePoint(parts[0]);
        Point p2 = parsePoint(parts[1]);
        return new Line(p2.y - p1.y, p1.x - p2.x, p1.x * p2.y - p2.x * p1.y);
    }

    static Point parseExpression(String s) {
        s = s.trim();
        if (s.contains("@")) {
            String[] parts = s.split("@");
            if (parts.length == 2) {
                Point p1 = parsePoint(parts[0]);
                Point p2 = parsePoint(parts[1]);
                return new Point(p1.x + p2.x, p1.y + p2.y);
            } else if (parts.length == 3) {
                Point p1 = parsePoint(parts[0]);
                Line l1 = parseLine(parts[1]);
                Point p2 = parsePoint(parts[2]);
                return new Point(p1.x + l1.a * p2.x + l1.b * p2.y + l1.c, p1.y - l1.a * p2.x - l1.b * p2.y + l1.c);
            } else if (parts.length == 4) {
                Line l1 = parseLine(parts[0]);
                Point p1 = parsePoint(parts[1]);
                Line l2 = parseLine(parts[2]);
                return new Point((l1.a * l2.c - l2.a * l1.c) / (l1.a * l2.b - l2.a * l1.b), (l1.b * l2.c - l2.b * l1.c) / (l1.a * l2.b - l2.a * l1.b));
            }
        } else {
            return parsePoint(s);
        }
        return null;
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("#")) {
                break;
            }
            Point p = parseExpression(line);
            System.out.printf("%.12f %.12f%n", p.x, p.y);
        }
    }
}
