#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define NMAX 105
#define MMAX 1005
#define INF 0x3f3f3f3f

int n, m, T;
int p[NMAX], t[NMAX], k[NMAX];
int a[MMAX], b[MMAX];
int d[NMAX][NMAX], f[NMAX][NMAX];

void init() {
    int i, j;
    for (i = 0; i <= n; i++) {
        for (j = 0; j <= n; j++) {
            d[i][j] = INF;
        }
    }
    for (i = 0; i <= n; i++) {
        for (j = 0; j <= n; j++) {
            f[i][j] = 0;
        }
    }
}

void dijkstra(int s) {
    int i, j, k;
    for (i = 0; i <= n; i++) {
        for (j = 0; j <= n; j++) {
            d[i][j] = INF;
        }
    }
    d[s][0] = 0;
    for (k = 0; k <= n; k++) {
        int u = k;
        for (i = 0; i <= n; i++) {
            for (j = 0; j <= n; j++) {
                if (d[i][j] < INF && (u == n || d[i][j] < d[u][j])) {
                    u = i;
                }
            }
        }
        for (i = 0; i <= n; i++) {
            for (j = 0; j <= n; j++) {
                if (d[u][j] < INF && d[u][j] + d[i][j] < d[i][j]) {
                    d[i][j] = d[u][j] + d[i][j];
                }
            }
        }
    }
}

int main() {
    int Tc;
    scanf("%d", &Tc);
    while (Tc--) {
        scanf("%d%d%d", &n, &m, &T);
        for (int i = 1; i <= n; i++) {
            scanf("%d%d%d", &p[i], &t[i], &k[i]);
        }
        for (int i = 1; i <= m; i++) {
            scanf("%d%d", &a[i], &b[i]);
        }
        init();
        for (int i = 1; i <= m; i++) {
            d[a[i]][b[i]] = 1;
        }
        dijkstra(0);
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                f[i][j] = d[i][j];
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= k[i]; j++) {
                for (int k = 0; k <= n; k++) {
                    if (f[i][k] < INF && f[i][k] + (T - (k + 1) * t[i]) / t[i] >= 0) {
                        f[i][k + 1] = max(f[i][k + 1], f[i][k] + (T - (k + 1) * t[i]) / t[i] * p[i]);
                    }
                }
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                ans = max(ans, f[i][j]);
            }
        }
        printf("%d\n", ans);
    }
    return 0;
}
