#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAXN = 105;
const int MAXM = 1005;

struct Edge {
    int to, next;
};

struct Node {
    int p, t, k;
};

int n, m, T;
int head[MAXN], tot;
Edge e[MAXM];
Node a[MAXN];
int vis[MAXN], dis[MAXN], cnt[MAXN];

void addEdge(int u, int v) {
    e[++tot].to = v;
    e[tot].next = head[u];
    head[u] = tot;
}

void dijkstra() {
    for (int i = 1; i <= n; i++) {
        dis[i] = 1e9;
        cnt[i] = 0;
    }
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>> q;
    q.push({0, 1});
    while (!q.empty()) {
        int u = q.top().second;
        q.pop();
        if (vis[u]) continue;
        vis[u] = 1;
        for (int i = head[u]; i; i = e[i].next) {
            int v = e[i].to;
            if (dis[v] > dis[u] + a[v].t) {
                dis[v] = dis[u] + a[v].t;
                q.push({dis[v], v});
            }
            if (dis[v] == dis[u] + a[v].t) {
                cnt[v] = min(cnt[u] + 1, a[v].k);
            }
        }
    }
}

int main() {
    while (cin >> n >> m >> T) {
        if (n == 0 && m == 0 && T == 0) break;
        tot = 0;
        for (int i = 1; i <= n; i++) {
            head[i] = 0;
            vis[i] = 0;
        }
        for (int i = 1; i <= n; i++) {
            cin >> a[i].p >> a[i].t >> a[i].k;
        }
        for (int i = 1; i <= m; i++) {
            int u, v;
            cin >> u >> v;
            addEdge(u, v);
        }
        dijkstra();
        long long ans = 0;
        for (int i = 1; i <= n; i++) {
            long long sum = 0;
            for (int j = 1; j <= cnt[i]; j++) {
                sum += min((long long)T / (dis[i] + j * a[i].t), (long long)a[i].k - j + 1) * a[i].p;
            }
            ans = max(ans, sum);
        }
        cout << ans << endl;
    }
    return 0;
}
