    P = []
    for _ in range(N):
        p, t, k = map(int, sys.stdin.readline().split())
        P.append((p, t, k))
    adj = [[] for _ in range(N)]
    for _ in range(M):
        u, v = map(int, sys.stdin.readline().split())
        u -= 1
        v -= 1
        adj[u].append(v)
        adj[v].append(u)

    dp = [[0, 0] for _ in range(N)]
    for i in range(N):
        for j in range(len(adj[i])):
            u, v = adj[i][j], i
            if P[u][2] > 0:
                if P[u][1] <= T:
                    if P[u][1] + dp[u][0] <= T:
                        dp[v][0] = max(dp[v][0], dp[u][0] + P[u][0])
                        dp[v][1] = max(dp[v][1], dp[u][1] + P[u][0])
                    else:
                        dp[v][1] = max(dp[v][1], dp[u][1] + P[u][0] * (T - P[u][1] - dp[u][0]) // P[u][1])
                else:
                    if P[u][1] + dp[u][0] <= T:
                        dp[v][0] = max(dp[v][0], dp[u][0] + P[u][0])
                    else:
                        dp[v][1] = max(dp[v][1], dp[u][1] + P[u][0] * (T - P[u][1] - dp[u][0]) // P[u][1])
                dp[v][1] = max(dp[v][1], dp[v][0] + P[v][0])
                P[v][2] -= 1
    print(max(dp[i][0] for i in range(N)))

while True:
    try:
        solve()
