#include <stdio.h>
#include <string.h>

#define MAX 128

int pow2[MAX];
int filter[MAX];
int filter2[MAX];

int main() {
    int i, j, k;
    int n;

    pow2[0] = 1;
    for (i = 1; i < MAX; i++) {
        pow2[i] = pow2[i - 1] * 2;
    }

    while (1) {
        char str[MAX];
        scanf("%s", str);
        if (strcmp(str, "#") == 0) {
            break;
        }

        for (i = 0; i < MAX; i++) {
            filter[i] = str[i] - '0';
        }

        for (i = 0; i < MAX; i++) {
            filter2[i] = 0;
            for (j = 0; j < 7; j++) {
                for (k = 0; k < 7; k++) {
                    if (j != 3) {
                        filter2[i] += filter[pow2[j] + pow2[k]] * pow2[j + k];
                    }
                }
            }
        }

        int flag = 1;
        for (i = 0; i < MAX; i++) {
            if (filter[i] != filter2[i]) {
                flag = 0;
                break;
            }
        }

        if (flag) {
            printf("yes\n");
        } else {
            printf("no\n");
        }
    }

    return 0;
}
