#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    string line;
    vector<string> filters;
    while (getline(cin, line)) {
        if (line == "#") break;
        filters.push_back(line);
    }

    for (int i = 0; i < filters.size(); i++) {
        string filter = filters[i];
        string filter2 = filters[i];
        for (int j = 0; j < 4; j++) {
            for (int k = 0; k < 32; k++) {
                int bit = (k >> j) & 1;
                int bit2 = (k >> (j + 4)) & 1;
                filter2[k] = (filter[k] == '1' && bit == 1) ? '1' : '0';
                filter2[k + 32] = (filter[k + 32] == '1' && bit2 == 1) ? '1' : '0';
            }
        }
        if (filter == filter2) cout << "yes" << endl;
        else cout << "no" << endl;
    }

    return 0;
}
