import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String line;
        while (scanner.hasNextLine()) {
            line = scanner.nextLine();
            if (line.equals("#")) {
                break;
            }
            int[][] filter = new int[8][8];
            for (int i = 0; i < 8; i++) {
                for (int j = 0; j < 8; j++) {
                    filter[i][j] = line.charAt(i * 8 + j) - '0';
                }
            }
            int[][] filter2 = new int[8][8];
            for (int i = 0; i < 8; i++) {
                for (int j = 0; j < 8; j++) {
                    filter2[i][j] = filter[filter[i][j]][j];
                }
            }
            boolean isIdempotent = true;
            for (int i = 0; i < 8; i++) {
                for (int j = 0; j < 8; j++) {
                    if (filter[i][j] != filter2[i][j]) {
                        isIdempotent = false;
                        break;
                    }
                }
                if (!isIdempotent) {
                    break;
                }
            }
            System.out.println(isIdempotent ? "yes" : "no");
        }
    }
}
