#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_POSTS 1000
#define MAX_MESSAGE 50

typedef struct Post {
    int parent;
    char message[MAX_MESSAGE + 1];
} Post;

void print_post(Post post, int depth) {
    for (int i = 0; i < depth; i++) {
        printf(".");
    }
    printf("%s\n", post.message);
}

int main() {
    int n;
    scanf("%d", &n);

    Post posts[MAX_POSTS];
    for (int i = 0; i < n; i++) {
        int parent;
        scanf("%d", &parent);
        scanf("%s", posts[i].message);
        posts[i].parent = parent;
    }

    for (int i = 0; i < n; i++) {
        if (posts[i].parent == 0) {
            print_post(posts[i], 0);
        }
    }

    for (int i = 0; i < n; i++) {
        if (posts[i].parent != 0) {
            print_post(posts[i], 1 + posts[posts[i].parent].message[0] == '.' ? 1 : 0);
        }
    }

    return 0;
}
