import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        Map<Integer, String> posts = new HashMap<>();
        Map<Integer, Integer> parent = new HashMap<>();
        for (int i = 0; i < n; i++) {
            int k = scanner.nextInt();
            String m = scanner.next();
            posts.put(i, m);
            parent.put(i, k);
        }
        printTree(posts, parent, 0, 0);
    }

    public static void printTree(Map<Integer, String> posts, Map<Integer, Integer> parent, int depth, int index) {
        if (index >= posts.size()) return;
        System.out.println(repeat(".", depth) + posts.get(index));
        printTree(posts, parent, depth + 1, parent.get(index));
        printTree(posts, parent, depth + 1, index + 1);
    }

    public static String repeat(String str, int count) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < count; i++) {
            sb.append(str);
        }
        return sb.toString();
    }
}
