class Post:
    def __init__(self, parent, message):
        self.parent = parent
        self.message = message
        self.children = []

    def add_child(self, child):
        self.children.append(child)

    def get_depth(self):
        if self.parent is None:
            return 0
        else:
            return 1 + self.parent.get_depth()

    def __str__(self, depth=0):
        return '.' * depth + self.message


def parse_posts(posts):
    post_dict = {}
    for i in range(0, len(posts), 2):
        k, message = posts[i], posts[i + 1]
        post = Post(None, message)
        post_dict[k] = post
    for i in range(0, len(posts), 2):
        k, message = posts[i], posts[i + 1]
        post = post_dict[k]
        parent = post_dict.get(k - 1)
        if parent is not None:
            parent.add_child(post)
            post.parent = parent
    return post_dict[0]


def print_thread(post):
    print(post)
    for child in post.children:
        print(child)


def main():
    n = int(input())
    posts = []
    for _ in range(n):
        k = int(input())
        posts.append(k)
        message = input()
        posts.append(message)
    post = parse_posts(posts)
    print_thread(post)


if __name__ == "__main__":
    main()
