#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 1005
#define MAXM 505

int w, h, n;
int x1, y1, x2, y2;
int dx[MAXN], dy[MAXN];
int t[MAXN];
char s[MAXN][1005];

int min(int a, int b) { return a < b ? a : b; }

int get(int x, int y, int d) {
    if (x < 0 || x >= w || y < 0 || y >= h) return 0;
    return (s[y][x] >> d) & 1;
}

int main() {
    int i, j, k, x, y, ans = 0;
    scanf("%d %d %d", &w, &h, &n);
    scanf("%d %d %d %d", &x1, &y1, &x2, &y2);
    for (i = 0; i < n; i++) {
        scanf("%d %d", &x, &y);
        scanf("%d %s", &t[i], s[y]);
        dx[i] = 0; dy[i] = 0;
        for (j = 0; j < t[i]; j++) {
            switch (s[y][j]) {
                case 'U': dy[i]++; break;
                case 'D': dy[i]--; break;
                case 'L': dx[i]--; break;
                case 'R': dx[i]++; break;
            }
        }
    }
    for (i = 0; i < w; i++) {
        for (j = 0; j < h; j++) {
            int cnt = 0;
            for (k = 0; k < n; k++) {
                if (i + dx[k] >= 0 && i + dx[k] < w && j + dy[k] >= 0 && j + dy[k] < h) {
                    if (get(i + dx[k], j + dy[k], 0) || get(i + dx[k], j + dy[k], 1) ||
                        get(i + dx[k], j + dy[k], 2) || get(i + dx[k], j + dy[k], 3)) {
                        cnt++;
                    }
                }
            }
            if (i == x1 && j == y1) cnt = 0;
            if (i == x2 && j == y2) cnt = 0;
            if (i == w - 1) cnt += get(i, j, 2);
            if (j == h - 1) cnt += get(i, j, 3);
            ans = min(ans, cnt);
            for (k = 0; k < n; k++) {
                if (i + dx[k] >= 0 && i + dx[k] < w && j + dy[k] >= 0 && j + dy[k] < h) {
                    if (get(i + dx[k], j + dy[k], 0) || get(i + dx[k], j + dy[k], 1) ||
                        get(i + dx[k], j + dy[k], 2) || get(i + dx[k], j + dy[k], 3)) {
                        cnt--;
                    }
                }
            }
        }
    }
    printf("%d\n", ans);
    return 0;
}
