#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 1005;
const int M = 505;

int n, m, w, h, ans;
int x1, y1, x2, y2;
int sx[N], sy[N];
int t[N];
string s[N];

int dx[4] = {0, 0, -1, 1};
int dy[4] = {1, -1, 0, 0};

int get(int x, int y, int k) {
    return (x + dx[k]) % w + (y + dy[k]) % h * w;
}

int main() {
    cin >> w >> h >> n;
    cin >> x1 >> y1 >> x2 >> y2;
    if (x1 == w) x1 = w - 1;
    if (y1 == h) y1 = h - 1;
    if (x2 == w) x2 = w - 1;
    if (y2 == h) y2 = h - 1;
    for (int i = 1; i <= n; i++) {
        cin >> sx[i] >> sy[i];
        cin >> t[i] >> s[i];
    }
    for (int i = 1; i <= n; i++) {
        int cnt = 0;
        for (int j = 0; j < t[i]; j++) {
            int k = s[i][j] - 'U';
            int x = sx[i] + dx[k];
            int y = sy[i] + dy[k];
            if (x < 0 || x >= w || y < 0 || y >= h) continue;
            int id = get(x, y, k);
            if (id == get(x1, y1, k) || id == get(x2, y2, k)) cnt++;
        }
        ans = min(ans, cnt);
    }
    cout << ans << endl;
    return 0;
}
