import sys

def solve():
    W, H, N = map(int, input().split())
    x1, y1, x2, y2 = map(int, input().split())
    if x1 == W:
        x1 -= 1
    if y1 == H:
        y1 -= 1
    if x2 == W:
        x2 -= 1
    if y2 == H:
        y2 -= 1

    dp = [[0] * (H + 1) for _ in range(W + 1)]
    for _ in range(N):
        x, y = map(int, input().split())
        if x == W:
            x -= 1
        if y == H:
            y -= 1
        T, pattern = input().split()
        T = int(T)
        pattern = pattern * T
        for i in range(W):
            for j in range(H):
                if (i, j) == (x, y):
                    dp[i][j] += 1
                if (i, j) == (x + 1, y):
                    dp[i][j] += pattern.count('R')
                if (i, j) == (x - 1, y):
                    dp[i][j] += pattern.count('L')
                if (i, j) == (x, y + 1):
                    dp[i][j] += pattern.count('D')
                if (i, j) == (x, y - 1):
                    dp[i][j] += pattern.count('U')
                if (i, j) == (x + 1, y + 1):
                    dp[i][j] += pattern.count('R') + pattern.count('D')
                if (i, j) == (x + 1, y - 1):
                    dp[i][j] += pattern.count('R') + pattern.count('U')
                if (i, j) == (x - 1, y + 1):
                    dp[i][j] += pattern.count('L') + pattern.count('D')
                if (i, j) == (x - 1, y - 1):
                    dp[i][j] += pattern.count('L') + pattern.count('U')

    ans = float('inf')
    for i in range(x1, x2 + 1):
        for j in range(y1, y2 + 1):
            ans = min(ans, dp[i][j])
    print(ans)

if __name__ == '__main__':
    solve()
