#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct {
    int x, y, w;
} Point;

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int N;
    scanf("%d", &N);

    Point *points = (Point *)malloc(N * sizeof(Point));
    for (int i = 0; i < N; i++) {
        scanf("%d %d %d", &points[i].x, &points[i].y, &points[i].w);
    }

    int max_value = 0;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            int dx = abs(points[i].x - points[j].x);
            int dy = abs(points[i].y - points[j].y);
            if (dx == 0 || dy == 0) continue;
            int value = points[i].w * points[j].w / (dx * dy);
            if (value > max_value) max_value = value;
        }
    }

    int g = gcd(max_value, dx * dy);
    printf("%d / %d\n", max_value / g, (dx * dy) / g);

    free(points);
    return 0;
}
