#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

typedef long long ll;

struct Point {
    ll x, y, w;
};

bool comparePoints(const Point& a, const Point& b) {
    return a.x < b.x || (a.x == b.x && a.y < b.y);
}

ll gcd(ll a, ll b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    cin >> n;

    vector<Point> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].x >> points[i].y >> points[i].w;
    }

    sort(points.begin(), points.end(), comparePoints);

    ll maxArea = 0;
    ll maxFoxes = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (points[i].x == points[j].x || points[i].y == points[j].y) continue;

            ll area = abs(points[i].x - points[j].x) * abs(points[i].y - points[j].y);
            ll foxes = points[i].w + points[j].w;

            if (area > maxArea) {
                maxArea = area;
                maxFoxes = foxes;
            } else if (area == maxArea) {
                maxFoxes = max(maxFoxes, foxes);
            }
        }
    }

    if (maxFoxes == 0) {
        cout << "0 / 1" << endl;
    } else {
        ll g = gcd(maxFoxes, maxArea);
        cout << maxFoxes / g << " / " << maxArea / g << endl;
    }

    return 0;
}
