import java.io.*;
import java.util.*;

public class Main {
    static class Pair implements Comparable<Pair> {
        int x, y, w;

        public Pair(int x, int y, int w) {
            this.x = x;
            this.y = y;
            this.w = w;
        }

        @Override
        public int compareTo(Pair o) {
            return Integer.compare(x, o.x);
        }
    }

    static long gcd(long a, long b) {
        return b == 0 ? a : gcd(b, a % b);
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        Pair[] a = new Pair[n];
        for (int i = 0; i < n; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            a[i] = new Pair(Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()), Integer.parseInt(st.nextToken()));
        }
        Arrays.sort(a);
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (a[j].x == a[i].x || a[j].y == a[i].y) continue;
                long g = gcd(Math.abs(a[j].x - a[i].x), Math.abs(a[j].y - a[i].y));
                long N = a[i].w + a[j].w;
                long d = Math.abs(a[j].x - a[i].x) * Math.abs(a[j].y - a[i].y);
                ans = Math.max(ans, N / d);
            }
        }
        System.out.println(ans + " / 1");
    }
}
