import math
from functools import reduce
from operator import mul

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    return a * b // gcd(a, b)

def main():
    N = int(input())
    points = []
    for _ in range(N):
        x, y, w = map(int, input().split())
        points.append((x, y, w))

    max_value = 0
    for i in range(N):
        for j in range(i + 1, N):
            x1, y1, w1 = points[i]
            x2, y2, w2 = points[j]
            if x1 == x2 or y1 == y2:
                continue
            dx = abs(x2 - x1)
            dy = abs(y2 - y1)
            value = w1 * w2 // lcm(dx, dy)
            max_value = max(max_value, value)

    if max_value == 0:
        print("0 / 1")
    else:
        g = reduce(gcd, [max_value] + [p[2] for p in points])
        print(f"{max_value // g} / {g}")

if __name__ == "__main__":
    main()
