#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

typedef struct {
    int x1, x2;
} Point;

int cmp(const void *a, const void *b) {
    Point *p1 = (Point *)a;
    Point *p2 = (Point *)b;
    return p1->x1 - p2->x1;
}

int main() {
    int N;
    scanf("%d", &N);
    Point *P = (Point *)malloc(N * sizeof(Point));
    for (int i = 0; i < N; i++) {
        scanf("%d %d %d %d", &P[i].x1, &P[i].x2, &P[i].x1, &P[i].x2);
    }
    qsort(P, N, sizeof(Point), cmp);
    int cnt = 1;
    int ans = 1;
    for (int i = 1; i < N; i++) {
        if (P[i].x1 >= P[i - 1].x2) {
            cnt++;
            ans = (cnt > ans) ? cnt : ans;
        } else {
            cnt = 1;
        }
    }
    printf("%d\n", ans);
    free(P);
    return 0;
}
