#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> xLower(n * 2);
    std::vector<int> xUpper(n * 2);

    for (int i = 0; i < n; ++i) {
        int xLower1, xLower2, xUpper1, xUpper2;
        std::cin >> xLower1 >> xLower2 >> xUpper1 >> xUpper2;
        xLower[2 * i] = xLower1;
        xLower[2 * i + 1] = xLower2;
        xUpper[2 * i] = xUpper1;
        xUpper[2 * i + 1] = xUpper2;
    }

    std::sort(xLower.begin(), xLower.end());
    std::sort(xUpper.begin(), xUpper.end());

    int count = 0;
    int i = 0;
    int j = 0;

    while (i < n * 2 && j < n * 2) {
        if (xLower[i] <= xUpper[j]) {
            ++count;
            ++i;
        } else {
            ++j;
        }
    }

    std::cout << count << std::endl;

    return 0;
}
