import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] xLower = new int[2 * n];
        int[] xUpper = new int[2 * n];

        for (int i = 0; i < n; i++) {
            xLower[2 * i] = scanner.nextInt();
            xLower[2 * i + 1] = scanner.nextInt();
            xUpper[2 * i] = scanner.nextInt();
            xUpper[2 * i + 1] = scanner.nextInt();
        }

        Arrays.sort(xLower);
        Arrays.sort(xUpper);

        int count = 0;
        int i = 0;
        int j = 0;

        while (i < 2 * n && j < 2 * n) {
            if (xLower[i] <= xUpper[j]) {
                count++;
                i++;
            } else {
                j++;
            }
        }

        System.out.println(count);
    }
}
