#include <stdio.h>
#include <stdlib.h>

#define MAX_N 100

int min_eval[MAX_N];
int max_eval[MAX_N];
int p[MAX_N];
int k[MAX_N];
int t[MAX_N][6];

void dfs(int node, int alpha, int beta) {
    if (k[node] == 0) {
        min_eval[node] = 1;
        max_eval[node] = 1;
        return;
    }

    int i, val;
    int min_val = 0;
    int max_val = 0;
    int alpha_new = alpha;
    int beta_new = beta;

    for (i = 0; i < k[node]; i++) {
        val = -dfs(t[node][i], -beta, -alpha);
        if (val >= beta_new) {
            min_eval[node] = 1 + min_eval[t[node][i]];
            max_eval[node] = 1 + max_eval[t[node][i]];
            return;
        }
        if (val > alpha_new) {
            alpha_new = val;
            min_val = 1 + min_val;
            max_val = 1 + max_val;
        }
        min_eval[node] = 1 + min_eval[t[node][i]];
        max_eval[node] = 1 + max_eval[t[node][i]];
    }

    if (alpha_new == alpha) {
        min_eval[node] = min_val;
    } else {
        min_eval[node] = min_val + 1;
    }

    if (beta_new == beta) {
        max_eval[node] = max_val;
    } else {
        max_eval[node] = max_val + 1;
    }
}

int main() {
    int n, i, j;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        scanf("%d", &p[i]);
    }
    for (i = 0; i < n; i++) {
        scanf("%d", &k[i]);
        for (j = 0; j < k[i]; j++) {
            scanf("%d", &t[i][j]);
            t[i][j]--;
        }
    }

    dfs(0, -100000, 100000);

    printf("%d %d\n", min_eval[0], max_eval[0]);

    return 0;
}
