#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 105;

int n, p[MAXN], k[MAXN], t[MAXN][6], cnt, ans1, ans2;

void dfs(int u, int alpha, int beta, bool flag) {
    if (k[u] == 0) {
        cnt++;
        return;
    }
    int tmp = cnt;
    for (int i = 0; i < k[u]; i++) {
        dfs(t[u][i], -beta, -alpha, flag);
        if (flag && cnt - tmp == 1) {
            ans1++;
        }
        if (!flag && cnt - tmp == 1) {
            ans2++;
        }
        if (flag && cnt - tmp > 1) {
            ans1 += cnt - tmp;
        }
        if (!flag && cnt - tmp > 1) {
            ans2 += cnt - tmp;
        }
    }
}

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) {
        cin >> p[i];
    }
    for (int i = 1; i <= n; i++) {
        cin >> k[i];
        for (int j = 0; j < k[i]; j++) {
            cin >> t[i][j];
        }
    }
    dfs(1, -100000, 100000, true);
    dfs(1, -100000, 100000, false);
    cout << ans1 << " " << ans2 << endl;
    return 0;
}
