import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static int n;
    static int[] p;
    static List<List<Integer>> g;
    static int[] k;
    static int[] t;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        n = Integer.parseInt(br.readLine());
        p = new int[n + 1];
        g = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            g.add(new ArrayList<>());
        }
        k = new int[n + 1];
        t = new int[n + 1];
        String[] line = br.readLine().split(" ");
        for (int i = 1; i <= n; i++) {
            p[i] = Integer.parseInt(line[i - 1]);
        }
        for (int i = 1; i <= n; i++) {
            line = br.readLine().split(" ");
            k[i] = Integer.parseInt(line[0]);
            for (int j = 1; j <= k[i]; j++) {
                t[i] = Integer.parseInt(line[j]);
                g.get(t[i]).add(i);
            }
        }
        int min = 0, max = 0;
        dfs(1, Integer.MIN_VALUE, Integer.MAX_VALUE, min, max);
        System.out.println(min + " " + max);
    }

    static void dfs(int u, int alpha, int beta, int min, int max) {
        if (k[u] == 0) {
            min++;
            max++;
            return;
        }
        int cnt = 0;
        for (int v : g.get(u)) {
            dfs(v, -beta, -alpha, min, max);
            cnt++;
            if (min > 0) {
                min++;
                max++;
                return;
            }
        }
        if (cnt == 1) {
            min++;
            max++;
        }
    }
}
