import sys

def negamax(node, alpha, beta, visited):
    if node in visited:
        return 0
    visited.add(node)
    if not children[node]:
        return leaf_values[node]
    min_eval = 0
    max_eval = 0
    for child in children[node]:
        eval = -negamax(child, -beta, -alpha, visited)
        if eval >= beta:
            return eval
        if eval > alpha:
            alpha = eval
        min_eval += 1
        max_eval += 1
    return alpha

def main():
    n = int(input())
    p = list(map(int, input().split()))
    leaf_values = {}
    children = {}
    for i in range(n):
        line = list(map(int, input().split()))
        k = line[0]
        children[i+1] = line[1:k+1]
        if k == 0:
            leaf_values[i+1] = p[i]
    min_eval = 0
    max_eval = 0
    for i in range(1, n+1):
        visited = set()
        alpha = -sys.maxsize - 1
        beta = sys.maxsize
        eval = -negamax(i, alpha, beta, visited)
        min_eval += 1 if eval != 0 else 0
        max_eval += 1
    print(min_eval, max_eval)

if __name__ == "__main__":
    main()
