#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_N 300
#define MAX_T 100000000

int n, t;
int a[MAX_N][MAX_N];
int v[MAX_N];
int ans[MAX_N];

void dfs(int x, int t) {
    if (t < 0) {
        return;
    }
    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (a[x][i] == 1 && ans[i] == 1) {
            cnt++;
        }
    }
    if (cnt % 2 == 1) {
        ans[x] = 1;
    } else {
        ans[x] = 0;
    }
    for (int i = 0; i < n; i++) {
        if (a[x][i] == 1) {
            dfs(i, t - 1);
        }
    }
}

int main() {
    int i, j;
    int flag = 0;
    int cnt = 0;
    scanf("%d", &n);
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            scanf("%d", &a[i][j]);
        }
    }
    for (i = 0; i < n; i++) {
        for (j = 0; j < n; j++) {
            scanf("%d", &v[j]);
        }
    }
    scanf("%d", &t);
    t = -t;
    for (i = 0; i < n; i++) {
        memset(ans, 0, sizeof(ans));
        ans[i] = v[i];
        dfs(i, t);
        int flag2 = 1;
        for (j = 0; j < n; j++) {
            if (ans[j] != v[j]) {
                flag2 = 0;
                break;
            }
        }
        if (flag2 == 1) {
            if (flag == 1) {
                printf("ambiguous\n");
                return 0;
            }
            flag = 1;
            for (j = 0; j < n; j++) {
                printf("%d ", ans[j]);
            }
            printf("\n");
            return 0;
        }
    }
    if (flag == 0) {
        printf("none\n");
    }
    return 0;
}
