import java.io.*;
import java.util.*;

public class Main {
    static final int N = 305;
    static int n, t;
    static int[][] a = new int[N][N];
    static int[] v = new int[N];
    static int[] ans = new int[N];
    static boolean[] vis = new boolean[N];
    static boolean flag;

    static void dfs(int u) {
        vis[u] = true;
        for (int i = 1; i <= n; i++) {
            if (a[u][i] == 1 && v[i] == 1) {
                flag = !flag;
            }
        }
        if (flag) {
            ans[u] = 1;
        } else {
            ans[u] = 0;
        }
        for (int i = 1; i <= n; i++) {
            if (a[u][i] == 1 && !vis[i]) {
                dfs(i);
            }
        }
    }

    static void solve(int t) {
        Arrays.fill(vis, false);
        Arrays.fill(ans, 0);
        flag = true;
        for (int i = 1; i <= n; i++) {
            if (!vis[i]) {
                dfs(i);
            }
        }
        for (int i = 1; i <= n; i++) {
            if (ans[i] != v[i]) {
                flag = false;
                break;
            }
        }
        if (flag) {
            for (int i = 1; i <= n; i++) {
                v[i] = ans[i];
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        n = Integer.parseInt(br.readLine());
        for (int i = 1; i <= n; i++) {
            String[] s = br.readLine().split(" ");
            for (int j = 1; j <= n; j++) {
                a[i][j] = Integer.parseInt(s[j - 1]);
            }
        }
        for (int i = 1; i <= n; i++) {
            v[i] = Integer.parseInt(br.readLine());
        }
        t = Integer.parseInt(br.readLine());
        for (int i = 1; i <= t; i++) {
            solve(i);
        }
        for (int i = 1; i <= n; i++) {
            if (v[i] == 1) {
                System.out.print(1 + " ");
            } else {
                System.out.print(0 + " ");
            }
        }
        System.out.println();
    }
}
