#include <stdio.h>
#include <stdlib.h>

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max(int a, int b) {
    return (a > b) ? a : b;
}

int main() {
    int n;
    scanf("%d", &n);

    int a[n], w[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &w[i]);
    }

    int dp[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[i][j] = 0;
        }
    }

    for (int i = 0; i < n; i++) {
        dp[i][i] = w[i];
    }

    for (int len = 2; len <= n; len++) {
        for (int i = 0; i < n - len + 1; i++) {
            int j = i + len - 1;
            for (int k = i; k < j; k++) {
                int left = (a[i] == 0 && a[k] == 1) || (a[i] == 1 && a[k] == 0);
                int right = (a[k] == 0 && a[j] == 1) || (a[k] == 1 && a[j] == 0);
                dp[i][j] = max(dp[i][j], min(dp[i][k], dp[k + 1][j]) + (left || right ? w[i] : 0));
            }
        }
    }

    printf("%d\n", dp[0][n - 1]);

    return 0;
}
